package Java_Assignment_Operators;

public class JavaAssignmentOperatorsDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x = 12;
		//incremental assignment operator
		System.out.println("incremental assignment operator :: " ); 
		System.out.println(x+=1);// x = x+1;
		
		int y = 20;
		//decremental assignment operator 
		System.out.println("decremental assignment operator  :: ");
		System.out.println(y-=2);  // y = y-2;
		
		int z = 12;
		//multiplicative assignment operator
		System.out.println("multiplicative assignment operator :: ");
		System.out.println(z*=2);  // z = z*2;
		
		int a = 4;
		//division assignment operator
		System.out.println("division assignment operator :: ");
		System.out.println(a/=1); // a=a/1;
		
		int b = 5;
		//mudulos assignment operator
		System.out.println("mudulos assignment operator :: ");
		System.out.println(b%=2);  // b = b%2;
		
		
		
	}

}
